bool Player::Trap()
{
	float x = myPosition.x; 
	float z = myPosition.z;
	int i = (floor(2*(x + 13.0f)));
	int j = (floor(2*(z + 7.5f)));
	if(i>55)
		i=55;
	if(j>57)
		j=57;
	if(xz[i][j]==2)//trap
		isAlive = false;
	return !isAlive;
}

void Player::Die()
{
	isAlive = false;
	PrintToOutputWindow("ARROW!");
	PrintToOutputWindow("--------");
	PrintToOutputWindow("--------");
	PrintToOutputWindow("CONGRATS YOU ARE DEAD");
	glutLeaveMainLoop();
}

bool Player::Win()
{
	if (treasures>=2)
		return true;
	else
		return false;
}

bool Player::checkCollision(float x,float z)
{
	if (x<0)
		x -= 0.1f;
	if (x>0)
		x += 0.1f;
	if (z<0)
		z -= 0.1f;
	if (z>0)
		z += 0.1f;
	int i = (floor(2*(x + 13.0f)));
	int j = (floor(2*(z + 7.5f)));
	if(i>55)
		i=55;
	if(j>57)
		j=57;
	PrintToOutputWindow("%f %f %d %d %d",x,z,i,j,xz[i][j]);
	if(xz[i][j]==0)
	{
		return true;//collision
	}
	else if(xz[i][j]==1)
	{
		return false;
	}
	else if(xz[i][j]==3)//treasure
	{
		treasures++;
		xz[i][j]=0;
		return true;
	}
	else 
		return true;
}

void Player::moveRight()
{
	r = glm::cross(front , glm::vec3(0,1,0));
	glm::vec3 xfront = front;
	front = glm::normalize(front + 2*v*r);

	float arotangle = acos(glm::dot(front,xfront)/(glm::length(front)*glm::length(xfront)));
	arotangle = arotangle * (180.0f/ 3.1415);
	rotangle -= arotangle;
}

void Player::moveLeft()
{
	r = glm::cross(front , glm::vec3(0,1,0));
	glm::vec3 xfront = front;
	front = glm::normalize(front + (-v)*2*r);

	float arotangle = acos(glm::dot(front,xfront)/(glm::length(front)*glm::length(xfront)));
	arotangle = arotangle * (180.0f/ 3.1415);
	rotangle += arotangle;
}

void Player::moveForward()
{
	newPosition = myPosition + v * front;
	if(!checkCollision(newPosition.x,newPosition.z))
	{	
		myPosition = myPosition + v * front;
	} 
	//myPosition = myPosition + v * front;
}

void Player::moveBack()
{
	newPosition = myPosition + (-v) * front;
	if(!checkCollision(newPosition.x,newPosition.z))
	{
		myPosition = myPosition + (-v) * front;
	}
	//myPosition = myPosition + (-v) * front;
}
